import React, { useState, useEffect, useRef, Fragment } from 'react';
import { Row, Col, Empty, Modal, Card, Typography, Alert, Form, Input, Checkbox, Button, Space, Upload, Dropdown, Menu, Select, notification, Transfer, DatePicker, Avatar, message, InputNumber } from 'antd';
import { LeftOutlined, LoadingOutlined, MailOutlined, CloseOutlined, PlusOutlined, MinusCircleOutlined ,MobileOutlined } from '@ant-design/icons';
import CropImage from '../../../components/sharing/crop-image'
import TextEditor from '../../../components/sharing/text-editor'
import moment from 'moment';
import { connect } from 'dva';
import MultiImageInput from 'react-multiple-image-input';

const { Text } = Typography;
const { TextArea } = Input;
const timestemp = (new Date()).getTime();
const { RangePicker } = DatePicker;
const formItemLayout = { labelCol: { xs: { span: 24, }, sm: { span: 24, }, } };
const baseUrl = process.env.REACT_APP_ApiUrl
const AddEditEmployee = props => {
	const [form] = Form.useForm();
	let idAdd = props.match.params.id === undefined ? true : false
	const { dispatch, newsCategory } = props;
	const [visible, setVisible] = useState(false);
	const [galleryVisible,setVisibleGallery] = useState(false);
	const [imagesList, setImagesList] = useState([]);
	const [gallaryImagesList, setGallaryImagesList] = useState([]);
	const [catlist, setCatlist] = useState([]);
	const [subcatlist, setSubCatlist] = useState([]);
	const [productFeatures, setProductFeatures] = useState([]);
	const [detail, setDetail] = useState({});
	const [itemId, setItemId] = useState()
	const [imageUrl, setImageUrl] = useState('');
	const [picModel, setPicModel] = useState(false);
	const [loading, setLoading] = useState(false);
	const [InquiryData, setInquiryData] = useState('');
	let [Inquiry, setInquiry] = useState('');
	const [count, setCount] = useState(0)
	const [ecount, setECount] = useState(0)
	const [dcount, setDCount] = useState(0)
	const [btnDis, setBtnDis] = useState(false)
	const [colorImages, setColorImages] = useState({});
	const [manageStock,setManageStock] = useState(false);
	const [scheduleSale,setScheduleSale] = useState(false);
	const [galleryImageUriList,setGalleryImageUriList] = useState({});
    const [isManageStockCbk,setManageStockCbk] = useState(false);
    

	useEffect(() => {
		
		let unmounted = false;
		window.scroll(0, 0);
		props.dispatch({ type: 'users/getDetail' });
		if (props.match.params.id) {
			setItemId(props.match.params.id)

			
			DetailFun(props.match.params.id)
		} else {
			form.resetFields();
			setImageUrl('');
			setImagesList([])
		}
		return () => { unmounted = true; }
	}, [dispatch])

	useEffect(() => {
        let unmounted = false;
   		// setCatlist(newsCategory.list ? newsCategory.list.result : [])

		return () => { unmounted = true; }
	}, [props.users])

	const DetailFun = (id) => {
			props.dispatch({type: 'users/getDetail', payload: { _id: id, profile_id: id }});
	}

	const convertToFormData = (values) => {
		const formData = new FormData();
		formData.append("avatar",values.avatar)
		formData.append("dob",values.dob)
		formData.append("email",values.email)
		formData.append("employee_type",values.employee_type)
		formData.append("mobile_number",values.mobile_number)
		formData.append("username",values.username)
	
		
		
		return formData;
	}

	const onFinish = val => {

		if(gallaryImagesList.length === 0) {
		  return	message.error('please upload profile image');
		}
		

		val.avatar = gallaryImagesList[0];
		console.log(val)
		console.log(gallaryImagesList[0].name)


		return;
		const formData = convertToFormData(val);
		if (props.match.params.id) {
			
			formData.append('_id',props.match.params.id);


			
			dispatch({ type: 'users/addEmployee', payload: formData });
		}
		else {
			dispatch({ type: 'user/editEmployee', payload: formData });
		}
	}

	useEffect(() => {
		
		
		let unmounted = false;

		let add = props.users.add
		if (!unmounted && add.count > count && add.data && add.data.status) {
			setCount(add.count);
			setBtnDis(false);
			setImageUrl('');
			setImagesList([])
			cancelFun();
			props.history.push('/employees');
		} else if (!unmounted && add.count > count && add.data && !add.data.status) {
			setBtnDis(false);
			setCount(add.count);
		}

		// Edit

		let edit = props.users.edit 




		if (!unmounted && edit.count > ecount && edit.message) {
			setECount(edit.count);
			setBtnDis(false);
			setImageUrl('');
			setImagesList([])
			cancelFun();
			props.history.push('/employees');
		} else if (!unmounted && edit.count > ecount && !edit.status) {
			setBtnDis(false);
			setECount(edit.count);
		}


		// detail
		if (props.match.params.id) {
			let detail = props.users.detail
			if (!unmounted && detail && detail.status) {
				setDCount(detail.count);
				let data = detail.data[0];
				setDetail(data)
				form.setFieldsValue({
					['title']: data.title, ['description']: data.description, ['category_id']: data.category_id, ['shortdesc']: data.shortdesc, ['thumbnail']: data.thumbnail
				});
				// if ( data.stock_quanlity || data.allow_back_orders || data.low_stock_threshold) {
				// 	setManageStock(true);
				// }

				if(data.images && data.images.file) {
					setImageUrl(data.images.file);
					setImagesList([{url: data.images.file}]);
				}

				if(data.images && data.images.length > 0) {
					setGalleryImageUriList(data.images.map((galImg) => {
						return process.env.REACT_APP_ApiUrl+'/'+galImg.file;
					}));
				}
			} else if (!unmounted && detail && !detail.status) {
				setBtnDis(false);
				setDCount(detail.count);
			}
		}
		return () => { unmounted = true; }
	}, [props.users])

	const cancelFun = () => {
		form.resetFields();
		setImageUrl('');
		setImagesList([])
		props.history.push('/employees');
	}
	const getNewImage = val => {
		console.log({val});
		setPicModel(false);
		setImageUrl(val.urls[0])
		form.setFieldsValue({ images: val.file })
	}
	const removeImgFun = () => {
		setImageUrl('');
		form.setFieldsValue({ images: [] });
	}

	const productHandle = (val, field, index) => {
		const product = productFeatures
		product.map((item, i) => {
			if (index == i) item[field] = val
		})
		setProductFeatures(product)
	}

	const uploadButton = (
		<div>
			{loading ? <LoadingOutlined /> : <i className="fad fa-camera-retro" style={{ color: "#13c2c2", fontSize: 57 }} />}
		</div>
	);

	const getBase64 = (img, callback) => {
		const reader = new FileReader();
		reader.addEventListener('load', () => callback(reader.result));
		reader.readAsDataURL(img);
	  }
	  


	  const dataURLtoFile = (dataurl, filename) => {
        var arr = dataurl.split(','),
            mime = arr[0].match(/:(.*?);/)[1],
            bstr = atob(arr[1]), 
            n = bstr.length, 
            u8arr = new Uint8Array(n);   
        while(n--){
            u8arr[n] = bstr.charCodeAt(n);
        }
        return new File([u8arr], filename, {type:mime});
    }
	
	
	  const onUploadGallery = (data) => {
		const images = Object.values(data).map((image,i) => {
			if(image.startsWith(process.env.REACT_APP_ApiUrl)) {
				return image;
			}else{
				return dataURLtoFile(image,'image_'+i);
			}
		})
		setGalleryImageUriList(data);
		const files = setGallaryImagesList([...images]);
	  }


	  const uploadButtonColor = (
		<div>
		  {loading ? <LoadingOutlined /> : <PlusOutlined />}
		  <div style={{ marginTop: 8 }}>Upload</div>
		</div>
	  );

	  const crop = {
		unit: '%',
		aspect: 4 / 3,
		width: '100'
	  };

	return (
		<Card title={<span><LeftOutlined onClick={() => props.history.push('/employees')} /> {idAdd ? 'Add Employee' : 'Edit Employee'}</span>} style={{ marginTop: "0" }}>

			<Form {...formItemLayout} form={form} name="loc_info" layout="vertical" onFinish={onFinish} className="innerFields">
			<Row gutter={15}>
				<Col sm={6} md={6}>
						<Form.Item  label={<span><span style={{ color: 'red' }}>*</span>Profile Images</span>} >
							 <MultiImageInput
									images={galleryImageUriList}
									setImages={onUploadGallery}
									allowCrop = {false}
									theme="light"
									max={1}
									cropConfig={{  minWidth: 10 , maxWidth: 2000  }}
									
    						/>
						</Form.Item>
					</Col>



				</Row>
				<Row gutter={15}>
			


					<Col flex="auto">
						<Row gutter={15}>
							<Col sm={12} md={12}>
								<Form.Item name="username" label="Employee Name" rules={[{ required: true, message: 'This field is required!' },{ max: 30, message: 'Employee Name must not be greater than 30 characters.' }]}  >
									<Input placeholder="Employee Name" />
								</Form.Item>
							</Col>

							<Col sm={12} md={12}>
							<Form.Item name="email" label="Email" rules={[
							{ 
								required: true, 
								message: 'Please input your Email!'
							},
							{
								type: 'email',
								message: 'The input is not valid E-mail!',
							}, 
						]} >
						<Input prefix={<MailOutlined className="site-form-item-icon" />} placeholder="Email" />
					  </Form.Item>
							</Col>
                           
						</Row>
					</Col>
			
				</Row>
				<Row gutter={15}>
				<Col flex="auto">
						<Row gutter={15}>
							<Col sm={12} md={12}>
							<Form.Item name="mobile_number" label="Mobile Number" rules={[ 
						 	{
							required: true,
								message: 'Please input your Mobile Number!', 
							},
							{
								pattern: /^[0-9]+$/,
								message: 'Need to enter number'
							},
							{ 
								len: 10, 
								message: 'Mobile number should be 10 digits long.' 
							},
						]} >
						<Input type="tel" prefix={<MobileOutlined className="site-form-item-icon" />} placeholder="Mobile Number" />
					  </Form.Item>
							</Col>

							<Col sm={12} md={12}>
								<Form.Item name="employee_type" label="Employee Type" rules={[{ required: true, message: 'This field is required!' },{ max: 30, message: 'Employee Type must not be greater than 30 characters.' }]}  >
									<Input placeholder="Employee Type" />
								</Form.Item>
							</Col>
                           
						</Row>
					</Col>
				</Row>

				<Row gutter={15}>
				<Col flex="auto">
						<Row gutter={15}>
					

							<Col sm={12} md={12}>
							<Form.Item name="dob" label="DOB" rules={[ { required: true, message: 'Field required!', }, ]} >
							<DatePicker  disabledDate={(current) => {
              let customDate = moment().format("YYYY-MM-DD");
              return current && current > moment(customDate, "YYYY-MM-DD");
            }}  format="YYYY-MM-DD"/>
						</Form.Item>
							</Col>
                           
						</Row>
					</Col>
				</Row>
				<Form.Item className="mb-0">
					<Button onClick={cancelFun}>Cancel</Button>&nbsp;&nbsp;
				<Button type="primary" disabled={btnDis} className="btn-w25 btn-primary-light" onClick={() => form.submit()}>Save</Button>
				</Form.Item>
			</Form>
		</Card>
	)
};

export default connect(({users }) => ({
	users:users,
	global: global

}))(AddEditEmployee);
